mapper.select:
SELECT order_id, store_id, member_id, status AS statusValue, take_in_out AS takeInOutValue, order_date
FROM orders
WHERE store_id = #{storeId}

mapper.update:
UPDATE orders
SET status = #{status}
WHERE store_id = #{storeId} AND order_id = #{orderId}

