mapper.select:
SELECT order_id, store_id, member_id, status, take_in_out, order_date
FROM orders
WHERE store_id = #{storeId}

mapper.update:
UPDATE orders
SET status = #{status}
WHERE store_id = #{storeId} AND order_id = #{orderId}

mapper.select:
SELECT order_id, store_id, member_id, status, take_in_out, order_date
FROM orders
WHERE store_id = #{storeId}

SELECT order_item_id, order_id, menu_id, quantity
FROM order_item
WHERE order_id = #{orderId}
